<!-- JavaScript für Dialog -->
<script>
function openDialog(type){
  const texts = {
    permafrost: {
      title: "Permafrost",
      text: "Rising temperatures causes degradation. Permafrost is like a clue, that keeps even the steepest slops and many otherwise unconsolidated sediments together. Without permafrost many slopes become unstable. Permafrost might be gone beneath altitudes of 3850m till 2100 under continued emission growths (Dumont et. al 2025)."
    },
    gletscher: {
      title: "Gletscher",
      text: "Instabile Gletscherseen können plötzlich ausbrechen (GLOFs)."
    },
    muren: {
      title: "Muren",
      text: "Schmelzender Permafrost destabilisiert Bergflanken."
    }
  };

  document.getElementById('dialog-title').textContent = texts[type].title;
  document.getElementById('dialog-text').textContent = texts[type].text;
  document.getElementById('dialog-container').style.display = 'block';
}
</script>

